import { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {

const [selectedYear, setSelectedYear] = useState("2022");

const saveSelectedValueHandler = (selected) => {
  setSelectedYear(selected);

}


    return (
    <div className="expenses">
    <ExpensesFilter filteredYear={selectedYear} onSaveSelectedValue={saveSelectedValueHandler}/>
      {props.items.map((item) => <ExpenseItem key={item.id} title={item.title} amount={item.amount} date={item.date} />)}
    </div>);;
}


export default Expenses;
